import React, { useState } from 'react';
import 'font-awesome/css/font-awesome.min.css';
import './sidebar.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHouseUser, faChartLine, faBoxArchive, faGear } from '@fortawesome/free-solid-svg-icons';
import { faUser } from '@fortawesome/free-regular-svg-icons'; 

const Sidebar = () => {

  const [activeIcon, setActiveIcon] = useState(null);

  const handleIconClick = (iconName) => {
    setActiveIcon(iconName);
  };

  return (
    <div className="sidebar">
      <ul>
        <li><a href="#"><FontAwesomeIcon icon={faHouseUser} /> Home Page</a></li>
        <li><a href="#"><FontAwesomeIcon icon={faUser} /> Recursos Humanos</a></li>
        <li><a href="#"><FontAwesomeIcon icon={faChartLine} /> Despesas</a></li>
        <li><a href="Estoque"><FontAwesomeIcon icon={faBoxArchive} /> Estoque</a></li>
        <li><a href="#"><FontAwesomeIcon icon={faGear} /> Suporte</a></li>
      </ul>
    </div>
  );
}

export default Sidebar;
